import React, { useState } from 'react';
import DashboAdmiardPage from './admin'; 
function LoginPage({ onLoginSuccess }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const loginData = { email, password };

    try {
      const response = await fetch('http://localhost:8000/api/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      if (response.ok) {
        const result = await response.json();
        localStorage.setItem('token', result.token);
        alert('Login successful');

        localStorage.setItem('token', result.token);
        onLoginSuccess();  // Notify parent about login success

        if (result.is_admin) {
          // If user is admin, redirect to admin page
          window.location.href = '/admin';  // Redirect to admin.js page
        } else {
          // If user is not admin, redirect to the home page or dashboard
          window.location.href = '/';  // Redirect to home/dashboard
        }
      } else {
        const result = await response.json();
        setError(result.error || 'Login failed');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('Something went wrong! Please try again later.');
    }
  };

  return (
    <div className="LoginPage">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default LoginPage;
