import React, { useState, useEffect } from 'react';

function AddBookForm() {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: '',
    publication_date: '',
    availability_status: 'Available', // Default value for availability status
  });
  const [books, setBooks] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [editBookId, setEditBookId] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const method = editMode ? 'PUT' : 'POST';
    const url = editMode
      ? `http://localhost:8000/api/books/${editBookId}/`
      : 'http://localhost:8000/api/books/';

    try {
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const result = await response.json();
      if (response.ok) {
        alert(result.message || 'Operation successful');
        fetchBooks(); 
        setEditMode(false);
        setFormData({
          title: '',
          author: '',
          genre: '',
          publication_date: '',
          availability_status: 'Available', // Reset to default
        });
      } else {
        alert('Error: ' + result.detail);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Something went wrong!');
    }
  };

  const fetchBooks = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/books/');
      const data = await response.json();
      setBooks(data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const handleEdit = (book) => {
    setFormData({
      title: book.title,
      author: book.author,
      genre: book.genre,
      publication_date: book.publication_date,
      availability_status: book.availability_status, // Only available when editing
    });
    setEditMode(true);
    setEditBookId(book.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this book?')) return;

    try {
      const response = await fetch(`http://localhost:8000/api/books/${id}/`, {
        method: 'DELETE',
      });

      if (response.ok) {
        alert('Book deleted successfully');
        fetchBooks(); 
      } else {
        alert('Failed to delete the book');
      }
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  useEffect(() => {
    fetchBooks(); // Fetch books when the component mounts
  }, []);

  return (
    <div className="AddBookForm">
      <h2>{editMode ? 'Edit Book' : 'Add Book'}</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Author:
          <input
            type="text"
            name="author"
            value={formData.author}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Genre:
          <input
            type="text"
            name="genre"
            value={formData.genre}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Publication Date:
          <input
            type="date"
            name="publication_date"
            value={formData.publication_date}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        
        {/* Only show Availability Status field when in edit mode */}
        {editMode && (
          <>
            <label>
              Availability Status:
              <select
                name="availability_status"
                value={formData.availability_status}
                onChange={handleChange}
              >
                <option value="Available">Available</option>
                <option value="Issued">Issued</option>
              </select>
            </label>
            <br />
          </>
        )}

        <button type="submit">{editMode ? 'Update Book' : 'Add Book'}</button>
      </form>

      <h2>View Books</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Publication Date</th>
            <th>Availability Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.length > 0 ? (
            books.map((book) => (
              <tr key={book.id}>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.genre}</td>
                <td>{book.publication_date}</td>
                <td>{book.availability_status}</td>
                <td>
                  <button onClick={() => handleEdit(book)}>Edit</button>
                  <button onClick={() => handleDelete(book.id)}>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No books available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
    
  );
}

export default AddBookForm;
