import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './style/navbar.css'; // Add styles here or create a separate CSS file

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Use useNavigate hook

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token && token.split('.').length === 3) {
      setIsLoggedIn(true); // Check if a valid token exists
    } else {
      setIsLoggedIn(false);
    }

    // Listen for changes in localStorage (for auto-refresh)
    const handleStorageChange = () => {
      const updatedToken = localStorage.getItem('token');
      if (updatedToken && updatedToken.split('.').length === 3) {
        setIsLoggedIn(true);
      } else {
        setIsLoggedIn(false);
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token'); // Clear the token from localStorage
    setIsLoggedIn(false);
    navigate('/login'); // Use navigate for redirection
  };

  return (
    <nav className="navbar">
      <ul>
        <li>
          <Link to="/">Welcome to the Library</Link>
        </li>
        {isLoggedIn ? (
          <>
            <li>
              <Link to="/booklist">Book List</Link>
            </li>
            <li>
              <Link to="/myissuedbooks">My Issued Books</Link>
            </li>
            <li>
              <button onClick={handleLogout}>Logout</button>
            </li>
          </>
        ) : (
          <>
            <li>
              <Link to="/login">Login</Link>
            </li>
            <li>
              <Link to="/signup">Sign Up</Link>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
