import React, { useState, useEffect } from 'react';

function MyIssuedBooksPage() {
  const [issuedBooks, setIssuedBooks] = useState([]);
  const decodedToken = jwtDecode(token);
    const userId = decodedToken.user_id; // Assuming your token includes user_id
  const fetchIssuedBooks = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/myissuebooks/');
      const data = await response.json();

    } catch (error) {
      console.error('Error fetching issued books:', error);
    }
  };

  useEffect(() => {
    fetchIssuedBooks(); // Fetch issued books when the component mounts
  }, []);

  return (
    <div className="MyIssuedBooksPage">
      <h2>My Issued Books</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Publication Date</th>
            <th>Availability Status</th>
          </tr>
        </thead>
        <tbody>
          {issuedBooks.length > 0 ? (
            issuedBooks.map((book) => (
              <tr key={book.id}>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.genre}</td>
                <td>{book.publication_date}</td>
                <td>{book.availability_status}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No issued books</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default MyIssuedBooksPage;
