import React, { useState, useEffect } from 'react';
import {jwtDecode} from 'jwt-decode'; // Corrected the import

function MyIssuedBooksPage() {
  const [issuedBooks, setIssuedBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token'); // Retrieve token from localStorage
    if (token) {
      const decodedToken = jwtDecode(token); // Decode the token to get user_id
      setUserId(decodedToken.user_id); // Assuming user_id is part of the JWT payload
    }
  }, []);

  useEffect(() => {
    if (userId) {
      const fetchIssuedBooks = async () => {
        try {
          const response = await fetch(`http://localhost:8000/api/myissuebooks/${userId}/`, {
            method: 'GET',
            headers: {
              'Authorization': `Token ${localStorage.getItem('token')}`,
            },
          });

          if (response.ok) {
            const data = await response.json();
            setIssuedBooks(data);
          } else {
            setError('Failed to fetch issued books');
          }
        } catch (error) {
          setError('Error fetching issued books');
          console.error('Error fetching issued books:', error);
        } finally {
          setLoading(false);
        }
      };

      fetchIssuedBooks();
    }
  }, [userId]);
  const ReturnBook = async (bookId) => {
    const token = localStorage.getItem('token'); // Assuming you store the token in localStorage
  
    if (!token) {
      alert('No token found. Please log in.');
      return;
    }
  
    // Decode the token to get user_id (if using JWT)
    const decodedToken = jwtDecode(token);
    const userId = decodedToken.user_id; // Assuming your token includes user_id
  
    const response = await fetch('http://localhost:8000/api/ret/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${token}`, // Include the token for authentication
      },
      body: JSON.stringify({
        book_id: bookId,
        user_id: userId,  // Pass the user_id
      }),
    });
  
  };
  
  if (!userId) {
    return <div>Please log in to view your issued books.</div>;
  }

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="BookListPage">
      <h2>My Issued Books</h2>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Publication Date</th>
            <th>Availability Status</th>
          </tr>
        </thead>
        <tbody>
          {issuedBooks.length > 0 ? (
            issuedBooks.map((book) => (
              <tr key={book.id}>
                <td>{book.book.title}</td>
                <td>{book.book.author}</td>
                <td>{book.book.genre}</td>
                <td>{book.book.publication_date}</td>
                <td>{book.book.availability_status}</td>
                <td>
                  <button
                    onClick={() => ReturnBook(book.id)}
                    disabled={book.book.availability_status !== 'Issued'}
                  >
                    Return
                  </button>
                  </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No issued books found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default MyIssuedBooksPage;
