import React, { useState, useEffect } from 'react';
import './style/BookListPage.css';
import {jwtDecode} from 'jwt-decode'; // Add jwt-decode package
function BookListPage() {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);
  const [loading,   setLoading] = useState(true);

  const fetchBooks = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/books/');
      const data = await response.json();
      setBooks(data);
    } catch (error) {
      setError('Error fetching books.');
      console.error('Error fetching books:', error);
    } finally {
      setLoading(false);
    }
  };

 

  const issueBook = async (bookId) => {
    const token = localStorage.getItem('token'); // Assuming you store the token in localStorage
  
    if (!token) {
      alert('No token found. Please log in.');
      alert
      return;
    }
  
    // Decode the token to get user_id (if using JWT)
    const decodedToken = jwtDecode(token);
    const userId = decodedToken.user_id; // Assuming your token includes user_id
  
    const response = await fetch('http://localhost:8000/api/issuebooks/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${token}`, // Include the token for authentication
      },
      body: JSON.stringify({
        book_id: bookId,
        user_id: userId,  // Pass the user_id
      }),
    });
  
    const data = await response.json();
  
    if (response.ok) {
      // Show success alert with detailed message
      alert(`Book issued successfully! Due date: ${data.due_date}`);
      fetchBooks(); // Re-fetch books to reflect the updated availability status
    } else {
      // Show error alert if the API responds with an error
      alert(data.error || 'Failed to issue book. Please try again.');
    }
  };
  
  useEffect(() => {
    fetchBooks(); // Fetch books when the component mounts
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="BookListPage">
      <h2>Book List</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Publication Date</th>
            <th>Availability Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {books.length > 0 ? (
            books.map((book) => (
              <tr key={book.id}>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.genre}</td>
                <td>{book.publication_date}</td>
                <td>{book.availability_status}</td>
                <td>
                  <button
                    onClick={() => issueBook(book.id)}
                    disabled={book.availability_status !== 'Available'}
                  >
                    Issue
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No books available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default BookListPage;
