import React, { useState } from 'react';
import './style/HomePage.css';

import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import BookListPage from './BookListPage';
import MyIssuedBooksPage from './MyIssuedBooksPage'; 
import AddBookForm from './Homepage'; 
import SignupPage from './signup'; 
import LoginPage from './login'; 
// import DashboardPage from './DashboardPage';  // Import your DashboardPage

function HomePage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);  // Track login state

  // This function will be called after successful login
  const handleLoginSuccess = () => {
    setIsLoggedIn(true);  // Set logged-in state to true
  };

  return (
    <Router>
      <div className="HomePage">
        <h1>Library Management System</h1>
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            {!isLoggedIn && <li><Link to="/signup">Signup</Link></li>} {/* Only show Signup if not logged in */}
            {!isLoggedIn && <li><Link to="/login">Login</Link></li>}   {/* Only show Login if not logged in */}
            <li><Link to="/booklist">Book List</Link></li>
            <li><Link to="/my-issued-books">My Issued Books</Link></li>
            <li><Link to="/addbook">Add Book</Link></li>
            {isLoggedIn && <li><Link to="/">Dashboard</Link></li>}  {/* Show Dashboard after login */}
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<h2>Welcome to the Library</h2>} />
          <Route path="/signup" element={<SignupPage />} />
          <Route path="/login" element={<LoginPage onLoginSuccess={handleLoginSuccess} />} />
          <Route path="/booklist" element={<BookListPage />} />
          <Route path="/my-issued-books" element={<MyIssuedBooksPage />} />
          <Route path="/addbook" element={<AddBookForm />} />
        </Routes>
      </div>
    </Router>
  );
}

export default HomePage;
