import React, { useState, useEffect } from 'react';
import {jwtDecode} from 'jwt-decode'; // Import jwt-decode package

function MyIssuedBooksPage() {
  const [issuedBooks, setIssuedBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token'); // Retrieve token from localStorage
    if (token) {
      const decodedToken = jwtDecode(token); // Decode the token to get user_id
      setUserId(decodedToken.user_id); // Assuming user_id is part of the JWT payload
    }
  }, []);

  useEffect(() => {
    if (userId) {
      const fetchIssuedBooks = async () => {
        try {
          const response = await fetch(`http://localhost:8000/api/myissuebooks/${userId}/`, {
            method: 'GET',
            headers: {
              'Authorization': `Token ${localStorage.getItem('token')}`,
            },
          });

          if (response.ok) {
            const data = await response.json();
            setIssuedBooks(data);
          } else {
            setError('Failed to fetch issued books');
          }
        } catch (error) {
          setError('Error fetching issued books');
          console.error('Error fetching issued books:', error);
        } finally {
          setLoading(false);
        }
      };

      fetchIssuedBooks();
    }
  }, [userId]);

  if (!userId) {
    return <div>Please log in to view your issued books.</div>;
  }

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="BookListPage">
      <h2>Book List</h2>
      <table>
  <thead>
    <tr>
      <th>Title</th>
      <th>Author</th>
      <th>Genre</th>
      <th>Publication Date</th>
      <th>Availability Status</th>
    </tr>
  </thead>
  <tbody>
    {issuedBooks.length > 0 ? (
      issuedBooks.map((book) => (
        <tr key={books.id}>
          {/* Access the title from the related book object */}
          <td>{books.book.title }</td>
          <td>{books.author || "Unknown Author"}</td>
          <td>{book.genre || "Unknown Genre"}</td>
          <td>{book.publication_date || "Unknown Date"}</td>
          <td>{book.availability_status || "Unknown Status"}</td>
        </tr>
      ))
    ) : (
      <tr>
        <td colSpan="5">No issued books found</td>
      </tr>
    )}
  </tbody>
</table>

    </div>
  );
}

export default MyIssuedBooksPage;
