import React, { useState, useEffect } from 'react';

function BookListPage() {
  const [books, setBooks] = useState([]);

  const fetchBooks = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/books/');
      const data = await response.json();
      setBooks(data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const handleIssue = async (bookId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/books/${bookId}/`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ availability_status: 'Issued' }),
      });

      const result = await response.json();
      if (response.ok) {
        alert('Book issued successfully');
        fetchBooks(); // Refresh the book list
      } else {
        alert('Error: ' + result.detail);
      }
    } catch (error) {
      console.error('Error issuing book:', error);
      alert('Something went wrong!');
    }
  };

  useEffect(() => {
    fetchBooks(); // Fetch books when the component mounts
  }, []);

  return (
    <div className="BookListPage">
      <h2>Book List</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Publication Date</th>
            <th>Availability Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.length > 0 ? (
            books.map((book) => (
              <tr key={book.id}>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.genre}</td>
                <td>{book.publication_date}</td>
                <td>{book.availability_status}</td>
                <td>
                  {book.availability_status === 'Available' && (
                    <button onClick={() => handleIssue(book.id)}>Issue</button>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No books available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default BookListPage;
